// ECMAscript 6 now has a formal definition for
// an array iterator that we can access through the
// "entries" method. The iterator has a "next"
// method that returns either
//  { done: true }
//  { value: [index, entry], done: false }
var a = [ 'Boris', new Date(), 1, true ],
    iterator = a.entries();

console.log(iterator.next());
console.log(iterator.next());
console.log(iterator.next());
console.log(iterator.next());
console.log(iterator.next());

// Before ECMAscript 6, we initialized an array
// by looping over it:
var a, i;
a = [ 1, 2, 3, 4, 5 ];
for (i = 0; i < a.length; i += 1) {
  a[i] = 'curtis';
}
console.log(a);

// ECMAscript 6 now gives us an optimized native
// implementation to achieve the same thing.
a = [ 1, 2, 3, 4, 4 ];
a.fill('ES6');
console.log(a);

// The new find method of proper Array instances
// allows you to provide a comparator function to
// find the first value that matches the
// comparator.
var a = [ 1, 'two', new Date(), null, {} ];

// Always return the second index, if the array
// is that long.
function secondIndex(value, index) {
  return index === 1;
}

// Never match anything. This will return
// undefined.
function nothing() {
  return false;
}

// Always match. This will return the first entry.
function always() {
  return true;
}

// Find a Date object.
function dateish(value) {
  var s = Object.prototype.toString.call(value);
  return s === '[object Date]';
}

console.log(a.find(secondIndex));
console.log(a.find(nothing));
console.log(a.find(always));
console.log(a.find(dateish));

// The new findIndex method of proper Array
// instances allows you to provide a comparator
// function to find the index of the first value
// that matches the comparator. This allows for a
// looser comparison that indexOf which uses
// strict equality.
var a = [ 1, 'two', new Date(), null, {} ];

// Always return 1 if the array has at least two
// values.
function secondIndex(value, index) {
  return index === 1;
}

// Never match anything. This will return -1.
function nothing() {
  return false;
}

// Always match. This will return 0 for non-empty
// arrays.
function always() {
  return true;
}

// Find a Date object. For the sake of this
// example, it will return 2.
function dateish(value) {
  var s = Object.prototype.toString.call(value);
  return s === '[object Date]';
}

console.log(a.findIndex(secondIndex));
console.log(a.findIndex(nothing));
console.log(a.findIndex(always));
console.log(a.findIndex(dateish));

