function old_way() {
  // In the old days, to convert the arguments
  // pseudo-array into a proper array, we had
  // to use the Array slice method like this.
  var a = Array.prototype.slice.call(arguments);
  console.log(a);
}

function new_way() {
  // Now, we have a more elegant solution with
  // Array.from.
  var a = Array.from(arguments);
  console.log(a);

  // We can even provide a mapping function.
  a = Array.from(arguments, function (x) {
    return x * x;
  });
  console.log(a);
}

old_way(1, 2, 3, 4, 5);
new_way(1, 2, 3, 4, 5);
